INSERT INTO `yh_non_static_pages_content` (`content_id`, `lang`, `action`, `criteria`, `meta_title`, `meta_keywords`, `meta_description`, `top_title`, `top_text`, `canonical`, `device`) VALUES
(259, 'bg', 'fast-search-results', 'imoti-prodajba-slaveykovo', 'Продава имоти Славейково | Янтра Хоумс', 'Славейково продава имоти, продавам имоти Славейково', 'Продажба на имоти в Славейково', 'Най-новите имоти за продажба в Славейково, община Дряново', '<p>Славейково е малко и спокойно селце с уникална природа, разположено в полите на Стара планина</p>\r\n<h3 style=\"font-weight: normal; margin-bottom: 0;\"><br />Селото се намира в много богат на туристически обекти район, само на 12 км от Дряново и на 18 км от Габрово.<span id=\"link_view\" class=\"clear_l pointer f_right mt_5px\" style=\"display: block;\" onclick=\"$(\'div#text_more\').slideToggle(); $(this).hide(); $(\'span#link_hide\').show();\"> <span class=\"link\">Прочетете повече за <strong>Славейково</strong></span> <img style=\"display: inline; float: none; width: 8px;\" src=\"application/images/arrow_down.png\" alt=\"Славейково имоти прочетете повече\" /> </span></h3>\r\n<div id=\"text_more\" class=\"clear_l mt_10px display_none fs_13px a_justify \" style=\"width: 672px; color: #4a4a4a; display: none;\">Славейково е китно и очарователно балканско селце. Инфраструктурата е добра, селото е електрифицирано и водоснабдено и е разположено в красив предпланински район, който предлага чудни гледки. Пътищата са в добро състояние. В селото има магазин, ресторант, поща и редовни автобуси до Дряново. Най-големите предимства на мястото са тишината, спокойствието, чистият въздух и екологично чистата околна среда, все чудесни условия за селски, пешеходен и еко-туризъм. Близо до селото преминава река Янтра, която осигурява отлични условия за всички запалени рибари. Постоянното население на Славейково е около 65 души, но има много семейства, които притежават къщи тук и идват на почивка.<br/><br/>\r\nМежду <strong>Славейково</strong> и съседното село Гостилица е открит античният град Дискодуратера. Името е тракийско и означава \"двойно яка крепост\". Тя е била емпориум - най-големият пазар в провинция Мизия. Дори и римляните запазват древното му име. Наскоро там са открити 200 находки, някои от които нямат аналог, както в България, така и Европа. Дискодуратера има потенциала да се превърне в обект на културно-исторически туризъм.  През селото и в близост до него преминават вело и пешеходни маршрути към Национален парк \"Централен Балкан”. Само на 10 минути от Славейково се намира гр. Дряново, където могат да се посетят Дряновският манастир \"Св. Архангел Михаил\" и пещерата \"Бачо Киро”. На близо е и архитектурно - етнографският музей \"Етър\", както и Габрово, който е голям и добре развит град и предлага всички удобства.<br/><br/>\r\nНай-близки градове: Дряново – 12км, Габрово – 18 км, Севлиево – 27 км, Велико Търново – 33 км<br/>\r\nНай-близки села: Скалско – 3 км, Гостилица – 4 км, Янтра – 8 км<br/>\r\nПощенски код: 5391<br/>\r\nПлощ: 11.648 km2<br/>\r\nНадморска височина: от 300 до 499<br/>\r\nКоординати: 42.983 N, 25.367 E<br/>\r\n<span id=\"link_hide\" class=\"clear_l pointer mt_5px f_right display_none\" style=\"display: none;\" onclick=\"$(\'div#text_more\').slideToggle(); $(this).hide(); $(\'span#link_view\').show();\"> <span class=\"link\">Затвори стр. за <strong>Славейково</strong></span> <img style=\"display: inline; float: none; width: 8px;\" src=\"application/images/arrow_up.png\" alt=\"Славейково - Затвори\" /> </span></div>', 'www.yantrahomes.com/bg/fast-search-results/imoti-prodajba-slaveykovo/page-1', 'Desktop'),
(260, 'bg', 'fast-search-results', 'imoti-prodajba-tsareva-livada', 'Продава имоти Царева ливада | Янтра Хоумс', 'Царева ливада продава имоти, продавам имоти Царева ливада', 'Продажба на имоти в Царева ливада', 'Най-новите имоти за продажба в Царева ливада, община Дряново', '<p>Царева ливада е добре развито село, разположено в полите на Средна Стара планина и на брега на река Дряновска, само на 6 км от град Дряново</p>\r\n<h3 style=\"font-weight: normal; margin-bottom: 0;\"><br />Селото разполага с не малко къщи за гости, предлагащи смесица от възрожденска архитектура и съвременни условия и в него успешно се развива селски туризъм.<span id=\"link_view\" class=\"clear_l pointer f_right mt_5px\" style=\"display: block;\" onclick=\"$(\'div#text_more\').slideToggle(); $(this).hide(); $(\'span#link_hide\').show();\"> <span class=\"link\">Прочетете повече за <strong>Царева ливада</strong></span> <img style=\"display: inline; float: none; width: 8px;\" src=\"application/images/arrow_down.png\" alt=\"Царева ливада имоти прочетете повече\" /> </span></h3>\r\n<div id=\"text_more\" class=\"clear_l mt_10px display_none fs_13px a_justify \" style=\"width: 672px; color: #4a4a4a; display: none;\">Царева ливада предоставя редовен железопътен транспорт до град Дряново, както и до цялата страна. Царева ливада е електрифицирано и водоснабдено село. В селото има кметство, целодневна детска градина, магазини и заведения. Населението му е около 700 човека.<br/><br/>\r\nПо време на управлението на Цар Иван Асен II лятната му резиденция е била разположена над селото. До нея е имало ливади, където са пасяли конете на царя. От там произлиза името на селото - <strong>Царева ливада</strong>. Близо до селото е историческата местност Стринава. Там са открити запазени останки от крепостни стени и основи на сгради. Родното място на българските царе Асен и Петър също е недалеч от Царева ливада.<br/><br/>\r\nУникалната природа на Балкана също привлича туристи от близки и далечни дестинации. Тук може да се развива успешен бизнес, като например селски еко туризъм. В близост до Царева ливада има много исторически и природни забележителности. Етнографският комплекс \"Старият град\" в Трявна. Обявен е за архитектурен резерват, запазил автентични къщи, калдъръмени улици, малки магазинчета и работилници. Само на 10 км от селото се намира Дряновският манастир, край който минава дряновската еко пътека. Пещерата Бачо Киро се намира на 5 минути от манастира. Формирала се е от водите на Дряновската река и представлява лабиринт с обща дължина на галериите около 2 400 м.<br/><br/>\r\nНай-близки градове: Дряново – 6 км, Трявна – 10 км, Габрово – 17 км, Велико Търново – 30 км<br/>\r\nНай-близки села: Добрените – 1 км, Долни Драгойча – 2 км, Искра – 2 км, Сяровци – 2 км<br/>\r\nПощенски код: 5380<br/>\r\nПлощ: 35,468 km2<br/>\r\nНадморска височина: 405 м<br/>\r\nКоординати: 42.932 N, 25.450 E<br/>\r\n<span id=\"link_hide\" class=\"clear_l pointer mt_5px f_right display_none\" style=\"display: none;\" onclick=\"$(\'div#text_more\').slideToggle(); $(this).hide(); $(\'span#link_view\').show();\"> <span class=\"link\">Затвори стр. за <strong>Царева ливада</strong></span> <img style=\"display: inline; float: none; width: 8px;\" src=\"application/images/arrow_up.png\" alt=\"Царева ливада - Затвори\" /> </span></div>', 'www.yantrahomes.com/bg/fast-search-results/imoti-prodajba-tsareva-livada/page-1', 'Desktop'),
(261, 'bg', 'fast-search-results', 'imoti-prodajba-turkincha', 'Продава имоти Туркинча | Янтра Хоумс', 'Туркинча продава имоти, продавам имоти Туркинча', 'Продажба на имоти в Туркинча', 'Най-новите имоти за продажба в Туркинча, община Дряново', '<p>Малкото и спокойно селце Туркинча е разположено в Дряновския балкан</p>\r\n<h3 style=\"font-weight: normal; margin-bottom: 0;\"><br />Селото се намира в живописен планински район и това го прави изключително атрактивно и предпочитано място за почивка и отдих.<span id=\"link_view\" class=\"clear_l pointer f_right mt_5px\" style=\"display: block;\" onclick=\"$(\'div#text_more\').slideToggle(); $(this).hide(); $(\'span#link_hide\').show();\"> <span class=\"link\">Прочетете повече за <strong>Туркинча</strong></span> <img style=\"display: inline; float: none; width: 8px;\" src=\"application/images/arrow_down.png\" alt=\"Туркинча имоти прочетете повече\" /> </span></h3>\r\n<div id=\"text_more\" class=\"clear_l mt_10px display_none fs_13px a_justify \" style=\"width: 672px; color: #4a4a4a; display: none;\">Туркинча е разположено само на 8,7 км. северно от красивия планински град Дряново. Селото е близо до главния път Велико Търново – Габрово и е само на минути от Керека, което е добре развито село, предлагащо всички необходими удобства като магазин, ресторант и кръчма.<br/><br/>\r\nЧистият въздух, горите и ливадите дават на <strong>Туркинча</strong> потенциала да стане курортно селище. Районът е подходящ за развитието на селски туризъм. Селото дава възможност за разходки, колоездене и пикници сред красивата природа. В близост се намират еко пътеки, Дряновският манастир и пещерата “Бачо Киро”, както и други исторически и природни забележителности.<br/><br/> \r\nРайонът около село Туркинча предлага уникална природа, чист планински въздух, тишина и спокойствие. Селото е малко и спокойно, така че тук ще можете да се наслаждавате на пълноценна почивка. Не изпускайте шанса да разгледате нашите имоти в това прекрасно село и да се сдоби